import { API_KEY } from './API_KEY';
import GoogleMapsLoader from 'google-maps';
GoogleMapsLoader.KEY = API_KEY;
export default {
  methods: {
    initSattelliteMap: function initSattelliteMap() {
      // Satellite Map
      var myLatlng = new window.google.maps.LatLng(40.748817, -73.985428);
      var mapOptions = {
        zoom: 3,
        scrollwheel: false,
        // we disable de scroll over the map, it is a really annoing when you scroll through page
        center: myLatlng,
        mapTypeId: window.google.maps.MapTypeId.SATELLITE
      };
      var map = new window.google.maps.Map(document.getElementById('satelliteMap'), mapOptions);
      var marker = new window.google.maps.Marker({
        position: myLatlng,
        title: 'Satellite Map!'
      });
      marker.setMap(map);
    },
    initRegularMap: function initRegularMap() {
      // Regular Map
      var myLatlng = new window.google.maps.LatLng(40.748817, -73.985428);
      var mapOptions = {
        zoom: 8,
        center: myLatlng,
        scrollwheel: false // we disable de scroll over the map, it is a really annoing when you scroll through page

      };
      var map = new window.google.maps.Map(document.getElementById('regularMap'), mapOptions);
      var marker = new window.google.maps.Marker({
        position: myLatlng,
        title: 'Regular Map!'
      });
      marker.setMap(map);
    },
    initCustomSkinMap: function initCustomSkinMap(google) {
      // Custom Skin & Settings Map
      var myLatlng = new google.maps.LatLng(40.748817, -73.985428);
      var mapOptions = {
        zoom: 13,
        center: myLatlng,
        scrollwheel: false,
        // we disable de scroll over the map, it is a really annoing when you scroll through page
        disableDefaultUI: true,
        // a way to quickly hide all controls
        zoomControl: true,
        styles: [{
          'featureType': 'water',
          'stylers': [{
            'saturation': 43
          }, {
            'lightness': -11
          }, {
            'hue': '#0088ff'
          }]
        }, {
          'featureType': 'road',
          'elementType': 'geometry.fill',
          'stylers': [{
            'hue': '#ff0000'
          }, {
            'saturation': -100
          }, {
            'lightness': 99
          }]
        }, {
          'featureType': 'road',
          'elementType': 'geometry.stroke',
          'stylers': [{
            'color': '#808080'
          }, {
            'lightness': 54
          }]
        }, {
          'featureType': 'landscape.man_made',
          'elementType': 'geometry.fill',
          'stylers': [{
            'color': '#ece2d9'
          }]
        }, {
          'featureType': 'poi.park',
          'elementType': 'geometry.fill',
          'stylers': [{
            'color': '#ccdca1'
          }]
        }, {
          'featureType': 'road',
          'elementType': 'labels.text.fill',
          'stylers': [{
            'color': '#767676'
          }]
        }, {
          'featureType': 'road',
          'elementType': 'labels.text.stroke',
          'stylers': [{
            'color': '#ffffff'
          }]
        }, {
          'featureType': 'poi',
          'stylers': [{
            'visibility': 'off'
          }]
        }, {
          'featureType': 'landscape.natural',
          'elementType': 'geometry.fill',
          'stylers': [{
            'visibility': 'on'
          }, {
            'color': '#b8cb93'
          }]
        }, {
          'featureType': 'poi.park',
          'stylers': [{
            'visibility': 'on'
          }]
        }, {
          'featureType': 'poi.sports_complex',
          'stylers': [{
            'visibility': 'on'
          }]
        }, {
          'featureType': 'poi.medical',
          'stylers': [{
            'visibility': 'on'
          }]
        }, {
          'featureType': 'poi.business',
          'stylers': [{
            'visibility': 'simplified'
          }]
        }]
      };
      var map = new google.maps.Map(document.getElementById('customSkinMap'), mapOptions);
      var marker = new google.maps.Marker({
        position: myLatlng,
        title: 'Custom Skin & Settings Map!'
      });
      marker.setMap(map);
    }
  },
  mounted: function mounted() {
    var _this = this;

    GoogleMapsLoader.load(function (google) {
      _this.initSattelliteMap(google);

      _this.initRegularMap(google);

      _this.initCustomSkinMap(google);
    });
  }
};
