import users from "@/pages/Dashboard/Tables/users.js";
import Fuse from "fuse.js";
import swal from "sweetalert2"; //COMPONENTES

import { StatsCard, NavTabsCard, TimeLine, TimeLineItem, PricingCard, TestimonialCard, Pagination } from "@/components";
export default {
  components: {
    StatsCard: StatsCard,
    NavTabsCard: NavTabsCard,
    PricingCard: PricingCard,
    TimeLine: TimeLine,
    TimeLineItem: TimeLineItem,
    TestimonialCard: TestimonialCard,
    Pagination: Pagination
  },
  props: {},
  data: function data() {
    return {
      users: users,
      checkbox1: null,
      currentSort: "Comprador",
      currentSortOrder: "asc",
      pagination: {
        perPage: 5,
        currentPage: 1,
        perPageOptions: [5, 10, 25, 50],
        total: 0,
        bottomPosition: 'md-bottom-left'
      },
      // footerTable: ["Name", "Email", "Age", "Salary", "Actions"],
      searchQuery: "",
      propsToSearch: ["Comprador", "Asunto", "total", "fecha"],
      tableData: users,
      searchedData: [],
      fuseSearch: null
    };
  },
  methods: {
    customSort: function customSort(value) {
      var _this = this;

      return value.sort(function (a, b) {
        var sortBy = _this.currentSort;

        if (_this.currentSortOrder === "desc") {
          return a[sortBy].localeCompare(b[sortBy]);
        }

        return b[sortBy].localeCompare(a[sortBy]);
      });
    },
    handleLike: function handleLike(item) {
      swal({
        title: "You liked ".concat(item.name),
        buttonsStyling: false,
        type: "success",
        confirmButtonClass: "md-button md-success"
      });
    },
    handleEdit: function handleEdit(item) {
      swal({
        title: "You want to edit ".concat(item.name),
        buttonsStyling: false,
        confirmButtonClass: "md-button md-info"
      });
    },
    handleDelete: function handleDelete(item) {
      var _this2 = this;

      swal({
        title: "Are you sure?",
        text: "You won't be able to revert this!",
        type: "warning",
        showCancelButton: true,
        confirmButtonClass: "md-button md-success btn-fill",
        cancelButtonClass: "md-button md-danger btn-fill",
        confirmButtonText: "Yes, delete it!",
        buttonsStyling: false
      }).then(function (result) {
        if (result.value) {
          _this2.deleteRow(item);

          swal({
            title: "Deleted!",
            text: "You deleted ".concat(item.name),
            type: "success",
            confirmButtonClass: "md-button md-success btn-fill",
            buttonsStyling: false
          });
        }
      });
    },
    deleteRow: function deleteRow(item) {
      var indexToDelete = this.tableData.findIndex(function (tableRow) {
        return tableRow.id === item.id;
      });

      if (indexToDelete >= 0) {
        this.tableData.splice(indexToDelete, 1);
      }
    }
  },
  mounted: function mounted() {
    this.onResponsiveInverted();
    window.addEventListener("resize", this.onResponsiveInverted); // Fuse search initialization.

    this.fuseSearch = new Fuse(this.tableData, {
      keys: ["name", "email"],
      threshold: 0.3
    });
  },
  beforeDestroy: function beforeDestroy() {
    window.removeEventListener("resize", this.onResponsiveInverted);
  },
  computed: {
    /***
     * Returns a page from the searched data or the whole data. Search is performed in the watch section below
     */
    queriedData: function queriedData() {
      var result = this.tableData;

      if (this.searchedData.length > 0) {
        result = this.searchedData;
      }

      return result.slice(this.from, this.to);
    },
    to: function to() {
      var highBound = this.from + this.pagination.perPage;

      if (this.total < highBound) {
        highBound = this.total;
      }

      return highBound;
    },
    from: function from() {
      return this.pagination.perPage * (this.pagination.currentPage - 1);
    },
    total: function total() {
      return this.searchedData.length > 0 ? this.searchedData.length : this.tableData.length;
    }
  },
  watch: {
    /**
     * Searches through the table data by a given query.
     * NOTE: If you have a lot of data, it's recommended to do the search on the Server Side and only display the results here.
     * @param value of the query
     */
    searchQuery: function searchQuery(value) {
      var result = this.tableData;

      if (value !== "") {
        result = this.fuseSearch.search(this.searchQuery);
      }

      this.searchedData = result;
    }
  }
};
